import React, { useRef, useEffect, useState, useContext } from "react";
import { WebSocketContext } from "../context/WebSocketContext";
import "./VideoCall.css";

// STUN servers for WebRTC
const ICE_SERVERS = {
  iceServers: [
    { urls: "stun:stun.l.google.com:19302" },
    { urls: "stun:stun1.l.google.com:19302" }
  ]
};

const VideoCall = ({ roomId, username }) => {
  const { sendMessage, lastMessage } = useContext(WebSocketContext);
  const [isCallActive, setIsCallActive] = useState(false);
  const [isInvitation, setIsInvitation] = useState(false);
  const [inviter, setInviter] = useState("");
  const [remoteParticipants, setRemoteParticipants] = useState([]);
  const [mediaErr, setMediaErr] = useState("");
  const localVideoRef = useRef(null);
  const localStream = useRef(null);
  const peerConnections = useRef({});
  const [isMuted, setIsMuted] = useState(false);
  const [isCameraOff, setIsCameraOff] = useState(false);

  // --- Respond to invitation if someone starts a call ---
  useEffect(() => {
    if (!lastMessage) return;
    let data = null;
    try { data = JSON.parse(lastMessage); } catch (err) { return; }
    if (!data) return;

    // Meeting invitation received
    if (data.type === "webrtc-start-invite" && data.roomId === roomId && data.inviter !== username && !isCallActive) {
      setIsInvitation(true);
      setInviter(data.inviter);
    }
  }, [lastMessage, roomId, isCallActive, username]);

  // ---- Start local media and join mesh as INVITEE ----
  const doJoinCallMesh = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true
      });
      localStream.current = stream;
      if (localVideoRef.current) localVideoRef.current.srcObject = stream;
      setIsCallActive(true);
      setIsInvitation(false);
      sendMessage(JSON.stringify({
        type: "webrtc-join",
        username,
        roomId
      }));
    } catch (err) {
      setMediaErr("Could not access camera or microphone. " + err.message);
    }
  };

  // ---- Start call for first user (HOST) ----
  const startCall = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true
      });
      localStream.current = stream;
      if (localVideoRef.current) localVideoRef.current.srcObject = stream;
      setIsCallActive(true);
      setIsInvitation(false);
      sendMessage(JSON.stringify({
        type: "webrtc-start-invite",
        inviter: username,
        roomId
      }));
      sendMessage(JSON.stringify({
        type: "webrtc-join",
        username,
        roomId
      }));
    } catch (err) {
      setMediaErr("Could not access camera or microphone. " + err.message);
    }
  };

  // --- ENSURE LOCAL VIDEO IS MOUNTED ---
  useEffect(() => {
    if (isCallActive && localVideoRef.current && localStream.current) {
      localVideoRef.current.srcObject = localStream.current;
    }
  }, [isCallActive]);

  // ---- PeerConnection (mesh) helpers ----
  const setupPeerConnection = (peerUser) => {
    const pc = new RTCPeerConnection(ICE_SERVERS);
    if (localStream.current) {
      localStream.current.getTracks().forEach((track) => {
        pc.addTrack(track, localStream.current);
      });
    }
    pc.ontrack = (event) => {
      setRemoteParticipants((prev) => {
        const exists = prev.some((p) => p.username === peerUser);
        if (!exists) {
          return [...prev, { username: peerUser, stream: event.streams[0] }];
        }
        return prev.map((p) =>
          p.username === peerUser ? { ...p, stream: event.streams[0] } : p
        );
      });
    };
    pc.onicecandidate = (event) => {
      if (event.candidate) {
        sendMessage(JSON.stringify({
          type: "webrtc-candidate",
          roomId,
          from: username,
          to: peerUser,
          candidate: event.candidate
        }));
      }
    };
    peerConnections.current[peerUser] = pc;
    return pc;
  };

  // --- Handle WebRTC signaling messages ---
  useEffect(() => {
    if (!lastMessage) return;
    let data = null;
    try { data = JSON.parse(lastMessage); } catch (err) { return; }
    if (!data) return;

    // Someone wants to join -- create offer for them
    if (data.type === "webrtc-join" && data.username !== username && isCallActive) {
      const pc = setupPeerConnection(data.username);
      pc.createOffer().then((offer) => {
        pc.setLocalDescription(offer);
        sendMessage(JSON.stringify({
          type: "webrtc-offer",
          roomId,
          from: username,
          to: data.username,
          offer
        }));
      });
    }

    // We received an offer --> answer
    if (data.type === "webrtc-offer" && data.to === username) {
      const pc = setupPeerConnection(data.from);
      pc.setRemoteDescription(new RTCSessionDescription(data.offer)).then(() => {
        return pc.createAnswer();
      }).then((answer) => {
        pc.setLocalDescription(answer);
        sendMessage(JSON.stringify({
          type: "webrtc-answer",
          roomId,
          from: username,
          to: data.from,
          answer
        }));
      });
    }

    // We received an answer to a previous offer
    if (data.type === "webrtc-answer" && data.to === username) {
      const pc = peerConnections.current[data.from];
      if (pc) {
        pc.setRemoteDescription(new RTCSessionDescription(data.answer));
      }
    }

    // Received ICE candidate (for faster connection/traversal)
    if (data.type === "webrtc-candidate" && data.to === username) {
      const pc = peerConnections.current[data.from];
      if (pc && data.candidate) {
        pc.addIceCandidate(new RTCIceCandidate(data.candidate));
      }
    }

    // Handle leaving (remote stream disappears)
    if (data.type === "webrtc-leave" && data.from) {
      if (peerConnections.current[data.from]) {
        peerConnections.current[data.from].close();
        delete peerConnections.current[data.from];
      }
      setRemoteParticipants((prev) =>
        prev.filter((p) => p.username !== data.from)
      );
    }
    // Clean up if someone closes tab
    window.onbeforeunload = () => {
      sendMessage(JSON.stringify({
        type: "webrtc-leave",
        from: username,
        roomId
      }));
      Object.values(peerConnections.current).forEach((pc) => pc.close());
      peerConnections.current = {};
    };
  }, [lastMessage, isCallActive, sendMessage, username, roomId]);

  // ---- End call (hang up) ----
  const endCall = () => {
    if (localStream.current) {
      localStream.current.getTracks().forEach((t) => t.stop());
    }
    Object.values(peerConnections.current).forEach((pc) => pc.close());
    peerConnections.current = {};
    sendMessage(JSON.stringify({
      type: "webrtc-leave",
      from: username,
      roomId
    }));
    setIsCallActive(false);
    setRemoteParticipants([]);
  };

  // ---- Control mute/video and UI ----
  const toggleMute = () => {
    if (!localStream.current) return;
    localStream.current.getAudioTracks().forEach((track) => {
      track.enabled = !track.enabled;
    });
    setIsMuted((m) => !m);
  };

  const toggleCamera = () => {
    if (!localStream.current) return;
    localStream.current.getVideoTracks().forEach((track) => {
      track.enabled = !track.enabled;
    });
    setIsCameraOff((c) => !c);
  };

  // ---- Show invitation to join the call ----
  if (isInvitation && !isCallActive) {
    return (
      <div className="video-call-invite-container">
        <div>
          <b>{inviter}</b> is inviting you to join the video call.
        </div>
        <button className="start-call-btn" onClick={doJoinCallMesh}>Join Call</button>
        <button className="end-call-btn" onClick={() => setIsInvitation(false)}>Reject</button>
      </div>
    );
  }

  return (
    <div className="video-call-container">
      {!isCallActive ? (
        <>
          {mediaErr && (
            <div style={{ color: "red" }}>{mediaErr}</div>
          )}
          <button className="start-call-btn" onClick={startCall}>
            ðŸ“¹ Start Video Call
          </button>
        </>
      ) : (
        <>
          <div className="video-grid">
            <div className="video-wrapper local-video">
              <video
                ref={localVideoRef}
                autoPlay
                muted
                playsInline
                className="video-element"
                style={{ filter: isCameraOff ? "grayscale(0.85)" : "none" }}
              />
              <div className="video-label" style={{ fontWeight: 600 }}>{username} (you)</div>
            </div>
            {remoteParticipants.map((p) => (
              <RemoteVideo
                key={p.username}
                stream={p.stream}
                user={p.username}
              />
            ))}
          </div>
          <div className="video-controls">
            <button
              className={`control-btn${isMuted ? " active" : ""}`}
              onClick={toggleMute}>
              {isMuted ? "ðŸ”‡ Unmute" : "ðŸ”Š Mute"}
            </button>
            <button
              className={`control-btn${isCameraOff ? " active" : ""}`}
              onClick={toggleCamera}>
              {isCameraOff ? "ðŸ“· Video Off" : "ðŸ“¹ Video On"}
            </button>
            <button
              className="control-btn end-call"
              onClick={endCall}>
              ðŸ“ž End Call
            </button>
          </div>
        </>
      )}
    </div>
  );
};

const RemoteVideo = ({ stream, user }) => {
  const ref = useRef(null);
  useEffect(() => {
    if (ref.current && stream) {
      ref.current.srcObject = stream;
    }
  }, [stream]);
  return (
    <div className="video-wrapper remote-video">
      <video ref={ref} autoPlay playsInline className="video-element" />
      <div className="video-label">{user}</div>
    </div>
  );
};

export default VideoCall;
