import React, { useRef, useEffect, useState, useContext } from 'react';
import { WebSocketContext } from '../context/WebSocketContext';

const Canvas = () => {
  const canvasRef = useRef(null);
  const [drawing, setDrawing] = useState(false);
  const [lastPosition, setLastPosition] = useState({ x: null, y: null });
  const { wsRef } = useContext(WebSocketContext);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    canvas.width = window.innerWidth * 0.8;
    canvas.height = window.innerHeight * 0.6;
    ctx.lineCap = 'round';
    ctx.lineWidth = 3;
    ctx.strokeStyle = '#000000';

    const receive = event => {
      const msg = JSON.parse(event.data);
      if (msg.type === 'draw') {
        ctx.beginPath();
        ctx.moveTo(msg.fromX, msg.fromY);
        ctx.lineTo(msg.toX, msg.toY);
        ctx.strokeStyle = msg.color;
        ctx.lineWidth = msg.thickness;
        ctx.stroke();
      }
    };

    if (wsRef.current) wsRef.current.addEventListener('message', receive);

    return () => {
      if (wsRef.current) wsRef.current.removeEventListener('message', receive);
    };
  }, [wsRef]);

  const start = e => {
    const { offsetX, offsetY } = e.nativeEvent;
    const ctx = canvasRef.current.getContext('2d');
    ctx.beginPath();
    ctx.moveTo(offsetX, offsetY);
    setDrawing(true);
    setLastPosition({ x: offsetX, y: offsetY });
  };

  const move = e => {
    if (!drawing) return;
    const { offsetX, offsetY } = e.nativeEvent;
    const ctx = canvasRef.current.getContext('2d');
    ctx.lineTo(offsetX, offsetY);
    ctx.stroke();

    if (wsRef.current && wsRef.current.readyState === 1 && lastPosition.x !== null) {
      wsRef.current.send(JSON.stringify({
        type: 'draw',
        fromX: lastPosition.x,
        fromY: lastPosition.y,
        toX: offsetX,
        toY: offsetY,
        color: ctx.strokeStyle,
        thickness: ctx.lineWidth
      }));
    }
    setLastPosition({ x: offsetX, y: offsetY });
  };

  const stop = () => {
    setDrawing(false);
    setLastPosition({ x: null, y: null });
  };

  return (
    <canvas
      ref={canvasRef}
      style={{
        border: '1px solid #999',
        background: '#fff',
        borderRadius: '8px'
      }}
      onMouseDown={start}
      onMouseMove={move}
      onMouseUp={stop}
      onMouseLeave={stop}
    />
  );
};

export default Canvas;
