import React, { useState, useContext } from "react";
import { WebSocketProvider, WebSocketContext } from "./context/WebSocketContext";
import Canvas from "./components/Canvas";
import LoginForm from "./components/LoginForm";
import RegisterForm from "./components/RegisterForm";

const AuthPage = () => {
  const [isRegistering, setIsRegistering] = useState(false);
  const context = useContext(WebSocketContext);
  const wsRef = context?.wsRef;

  if (wsRef && wsRef.current) {
    return <Canvas />;
  }

  return (
    <div>
      <h1>Collaborative Canvas Frontend</h1>
      {isRegistering ? (
        <>
          <RegisterForm onRegistered={() => setIsRegistering(false)} />
          <p>
            Already have an account?{" "}
            <button onClick={() => setIsRegistering(false)}>Sign In</button>
          </p>
        </>
      ) : (
        <>
          <LoginForm />
          <p>
            New here?{" "}
            <button onClick={() => setIsRegistering(true)}>Register</button>
          </p>
        </>
      )}
    </div>
  );
};

const App = () => (
  <WebSocketProvider>
    <AuthPage />
  </WebSocketProvider>
);

export default App;
