import React, { useState } from 'react';
import { WebSocketProvider } from './context/WebSocketContext';
import Canvas from './components/Canvas';
import LoginForm from './components/LoginForm';
import RegisterForm from './components/RegisterForm';

const App = () => {
  const [view, setView] = useState('login');
  const [user, setUser] = useState(null);
  const [roomId, setRoomId] = useState('');

  // Extracts the first name from the full name for personalization
  const getFirstName = (fullName) => {
    if (!fullName) return '';
    return fullName.trim().split(' ')[0];
  };

  // Handle login, expects userData to have at least { fullName, username }
  const handleLogin = (userData) => {
    const firstName = getFirstName(userData.fullName || userData.full_name);
    setUser({ ...userData, firstName });
    setView('room');
  };

  // Handle registration success
  const handleRegister = () => {
    setView('login');
  };

  // Personalized header
  const renderHeader = () => (
    <header style={{
      padding: '16px 0 0 0',
      textAlign: 'center',
      fontWeight: 700,
      fontSize: '1.8rem',
      color: '#2d3748',
      letterSpacing: '0.5px'
    }}>
      {user && user.firstName
        ? `${user.firstName}'s Real-Time Collaborative Canvas`
        : 'Real-Time Collaborative Canvas'}
    </header>
  );

  // Room selection UI
  const renderRoomSelector = () => (
    <div style={{ marginTop: 40 }}>
      <input
        type="text"
        placeholder="Enter room name"
        value={roomId}
        onChange={e => setRoomId(e.target.value)}
        style={{ fontSize: 18, padding: 8 }}
      />
      <button
        onClick={() => setView('canvas')}
        disabled={!roomId}
        style={{ padding: 8, fontSize: 18, marginLeft: 8 }}
      >
        Join Room
      </button>
    </div>
  );

  return (
    <WebSocketProvider roomId={roomId}>
      <div style={{
        background: '#f6f7fb',
        minHeight: '100vh',
        paddingBottom: 48
      }}>
        {renderHeader()}
        <main style={{
          margin: '32px auto',
          maxWidth: 980,
          minHeight: 500,
          background: '#fff',
          borderRadius: 18,
          boxShadow: '0 3px 24px #e1e8ed',
          padding: 28,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center'
        }}>
          {view === 'login' && (
            <LoginForm onSuccess={handleLogin} onSwitch={() => setView('register')} />
          )}
          {view === 'register' && (
            <RegisterForm onSuccess={handleRegister} onSwitch={() => setView('login')} />
          )}
          {view === 'room' && (
            renderRoomSelector()
          )}
          {view === 'canvas' && (
            <Canvas user={user} />
          )}
        </main>
      </div>
    </WebSocketProvider>
  );
};

export default App;
