import React, { useState } from 'react';

const RegisterForm = ({ onSuccess, onSwitch }) => {
  const [fullName, setFullName] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleRegister = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    try {
      const res = await fetch('http://localhost:8000/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ full_name: fullName, username, password })

      });
      const data = await res.json();
      if (res.ok) {
        setSuccess('Registration successful! Please login.');
        setTimeout(() => {
          if (onSuccess) onSuccess();
        }, 800);
      } else {
        if (Array.isArray(data.detail)) {
          setError(data.detail.map(e => e.msg).join(', '));
        } else if (typeof data.detail === 'string') {
          setError(data.detail);
        } else {
          setError('Registration failed');
        }
      }
    } catch {
      setError('Network error');
    }
  };

  return (
    <form onSubmit={handleRegister} style={{ display: 'flex', flexDirection: 'column', gap: 12 }}>
      <h2>Sign Up</h2>
      <input
        type="text"
        placeholder="Full Name"
        value={fullName}
        onChange={e => setFullName(e.target.value)}
        required
      />
      <input
        type="text"
        placeholder="Username"
        autoComplete="username"
        value={username}
        onChange={e => setUsername(e.target.value)}
        required
      />
      <input
        type="password"
        placeholder="Password"
        autoComplete="new-password"
        value={password}
        onChange={e => setPassword(e.target.value)}
        required
      />
      <button type="submit">Register</button>
      <button type="button" onClick={onSwitch} style={{ marginLeft: 0, background: "#eee" }}>
        Login
      </button>
      {error && <span style={{ color: 'red', marginTop: 8 }}>{error}</span>}
      {success && <span style={{ color: 'green', marginTop: 8 }}>{success}</span>}
    </form>
  );
};

export default RegisterForm;
