import React, { createContext, useEffect, useRef, useState } from 'react';

export const WebSocketContext = createContext(null);

export const WebSocketProvider = ({ children }) => {
  const [token, setToken] = useState(null);
  const wsRef = useRef(null);
  const [wsStatus, setWsStatus] = useState('disconnected'); // optional, for UI

  useEffect(() => {
    if (!token) return;

    let socket;
    let reconnectTimeout = null;

    function connect() {
      socket = new WebSocket(`ws://localhost:8000/ws?token=${token}`);

      wsRef.current = socket;
      setWsStatus('connecting');

      socket.onopen = () => {
        setWsStatus('connected');
        // Optionally: trigger a "re-join" or fetchHistory event
      };

      socket.onclose = () => {
        setWsStatus('disconnected');
        // Reconnect after 3s
        reconnectTimeout = setTimeout(connect, 3000);
      };

      socket.onerror = () => {
        setWsStatus('error');
        // Best practice: close and force reconnect
        if (socket.readyState === 1 || socket.readyState === 0) socket.close();
      };
    }

    connect();

    return () => {
      if (reconnectTimeout) clearTimeout(reconnectTimeout);
      if (wsRef.current) wsRef.current.close();
      wsRef.current = null;
      setWsStatus('disconnected');
    };
  }, [token]);

  return (
    <WebSocketContext.Provider value={{ wsRef, setToken, token, wsStatus }}>
      {children}
    </WebSocketContext.Provider>
  );
};
