import React, { useEffect, useState } from "react";
import "./RoomList.css";

// Helper to get JWT token from localStorage or cookie (customize as needed)
function getToken() {
  return localStorage.getItem("token"); // or however you store the JWT on login
}

const RoomList = ({ onJoin, user }) => {
  const [rooms, setRooms] = useState([]);
  const [newRoom, setNewRoom] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [adminRoom, setAdminRoom] = useState(""); // Track which room user is admin for

  const fetchRooms = () => {
    fetch("http://localhost:8000/rooms")
      .then((res) => res.json())
      .then((data) => setRooms(data.rooms || []));
  };

  useEffect(() => {
    fetchRooms();
  }, []);

  // --- CREATE ROOM WITH AUTH ---
  const handleCreateRoom = async () => {
    if (!newRoom.trim()) return;
    setError("");
    setSuccess("");
    try {
      const token = getToken();
      const resp = await fetch("http://localhost:8000/rooms", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ room: newRoom.trim() }),
      });
      const data = await resp.json();
      if (resp.ok) {
        setRooms((r) => [...new Set([...r, newRoom.trim()])]);
        setAdminRoom(newRoom.trim());
        setSuccess(`Room created. You are the admin!`);
        setNewRoom("");
        fetchRooms(); // reload rooms to reflect backend
      } else {
        setError(data.detail || "Room creation failed. Login required?");
      }
    } catch (e) {
      setError("Network error, try again.");
    }
  };

  // --- JOIN ROOM: Triggers parent handler ---
  const handleJoin = (room) => {
    onJoin(room);
  };

  return (
    <div className="roomlist-container">
      <h2 className="roomlist-title">Available Rooms</h2>
      <ul className="roomlist-list">
        {rooms.length === 0 && <li>No rooms yet.</li>}
        {rooms.map((room) => (
          <li key={room} className="roomlist-item">
            <span className="roomlist-room">
              {room}
              {(adminRoom === room) && user?.username &&
                <span style={{
                  background: "#3182ce", color: "#fff",
                  padding: "2px 7px", borderRadius: 8,
                  marginLeft: 8, fontSize: 13
                }}>
                  Admin (You)
                </span>
              }
            </span>
            <button
              className="roomlist-join-btn"
              onClick={() => handleJoin(room)}
            >
              Join
            </button>
          </li>
        ))}
      </ul>
      <div className="roomlist-create">
        <input
          type="text"
          value={newRoom}
          onChange={(e) => setNewRoom(e.target.value)}
          placeholder="Create new room"
          className="roomlist-input"
        />
        <button
          className="roomlist-create-btn"
          onClick={handleCreateRoom}
          disabled={!newRoom.trim()}
        >
          Create
        </button>
      </div>
      {error && <div style={{ color: "red", marginTop: 8 }}>{error}</div>}
      {success && <div style={{ color: "green", marginTop: 8 }}>{success}</div>}
    </div>
  );
};

export default RoomList;
