import React, { useState, useContext } from "react";
import { WebSocketProvider, WebSocketContext } from "./context/WebSocketContext";
import Canvas from "./components/Canvas";
import LoginForm from "./components/LoginForm";
import RegisterForm from "./components/RegisterForm";

const AuthPage = () => {
  const [showRegister, setShowRegister] = useState(false);
  const { ws } = useContext(WebSocketContext);

  if (ws) {
    return <Canvas />;
  }

  return (
    <div>
      <h1>Collaborative Canvas Frontend</h1>
      {showRegister ? (
        <>
          <RegisterForm onRegistered={() => setShowRegister(false)} />
          <p>
            Already have an account?{" "}
            <button onClick={() => setShowRegister(false)}>Sign In</button>
          </p>
        </>
      ) : (
        <>
          <LoginForm />
          <p>
            New here?{" "}
            <button onClick={() => setShowRegister(true)}>Register</button>
          </p>
        </>
      )}
    </div>
  );
};

const App = () => (
  <WebSocketProvider>
    <AuthPage />
  </WebSocketProvider>
);

export default App;
