import React, { useEffect, useState } from 'react';
import './RoomList.css';

const RoomList = ({ onJoin }) => {
  const [rooms, setRooms] = useState([]);
  const [newRoom, setNewRoom] = useState('');

  useEffect(() => {
    fetch('http://localhost:8000/rooms')
      .then(res => res.json())
      .then(data => setRooms(data.rooms || []));
  }, []);

  const handleCreateRoom = async () => {
    if (!newRoom.trim()) return;
    const resp = await fetch("http://localhost:8000/rooms", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ room: newRoom.trim() })
    });
    if (resp.ok) {
      setRooms(r => [...new Set([...r, newRoom.trim()])]);
      setNewRoom('');
    }
  };

  return (
    <div className="roomlist-container">
      <h2 className="roomlist-title">Available Rooms</h2>
      <ul className="roomlist-list">
        {rooms.length === 0 && <li>No rooms yet.</li>}
        {rooms.map(room => (
          <li key={room} className="roomlist-item">
            <span className="roomlist-room">{room}</span>
            <button className="roomlist-join-btn" onClick={() => onJoin(room)}>Join</button>
          </li>
        ))}
      </ul>
      <div className="roomlist-create">
        <input
          type="text"
          value={newRoom}
          onChange={e => setNewRoom(e.target.value)}
          placeholder="Create new room"
          className="roomlist-input"
        />
        <button
          className="roomlist-create-btn"
          onClick={handleCreateRoom}
          disabled={!newRoom.trim()}
        >
          Create Room
        </button>
      </div>
    </div>
  );
};

export default RoomList;
