import React, { useState } from 'react';
import LoginForm from './LoginForm';         // Import your login component
import RegisterForm from './RegisterForm';   // Import your register component
import CanvasApp from './CanvasApp';         // Replace with your actual main/canvas component
import './App.css';

const App = () => {
  // You might already have logic for session/token storage - here we'll just use state:
  const [user, setUser] = useState(null);
  const [showRegister, setShowRegister] = useState(false);

  // This gets the first name (everything before the first space) or falls back to the full name if there's no space
  const getFirstName = (fullName) => {
    if (!fullName) return '';
    return fullName.trim().split(' ')[0];
  };

  // After successful login, set the user object (could include fullName/username/etc.)
  const handleLoginSuccess = ({ fullName, username }) => {
    setUser({ fullName, username, firstName: getFirstName(fullName) });
  };

  // After successful registration, automatically show login form (optional: autofill username)
  const handleRegisterSuccess = () => setShowRegister(false);

  // Rendering
  if (!user) {
    return showRegister ? (
      <RegisterForm
        onSuccess={handleRegisterSuccess}
        onSwitch={() => setShowRegister(false)}
      />
    ) : (
      <LoginForm
        onSuccess={handleLoginSuccess}
        onSwitch={() => setShowRegister(true)}
      />
    );
  }

  // The heading is personalized per user below:
  return (
    <div className="App">
      <h1 className="canvas-title">
        {user.firstName ? `${user.firstName}'s Real-Time Collaborative Canvas` : 'Real-Time Collaborative Canvas'}
      </h1>
      <CanvasApp user={user} />  {/* Pass user info as needed */}
    </div>
  );
};

export default App;
