import React, { useState } from 'react';

const LoginForm = ({ onSuccess, onSwitch }) => {
  const [fullName, setFullName] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const res = await fetch('http://localhost:8000/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ full_name: fullName, username, password })
      });
      const data = await res.json();
      if (res.ok) {
        if (onSuccess) onSuccess({ fullname: fullName, username });
      } else {
        if (Array.isArray(data.detail)) {
          setError(data.detail.map(e => e.msg).join(', '));
        } else if (typeof data.detail === 'string') {
          setError(data.detail);
        } else {
          setError('Login failed');
        }
      }
    } catch {
      setError('Network error');
    }
  };

  return (
    <form onSubmit={handleLogin} style={{ display: 'flex', flexDirection: 'column', gap: 12 }}>
      <h2>Sign In</h2>
      <input
        type="text"
        placeholder="Full Name"
        value={fullName}
        onChange={e => setFullName(e.target.value)}
        required
      />
      <input
        type="text"
        placeholder="Username"
        autoComplete="username"
        value={username}
        onChange={e => setUsername(e.target.value)}
        required
      />
      <input
        type="password"
        placeholder="Password"
        autoComplete="current-password"
        value={password}
        onChange={e => setPassword(e.target.value)}
        required
      />
      <button type="submit">Login</button>
      <button type="button" onClick={onSwitch} style={{ marginLeft: 0, background: "#eee" }}>
        Register
      </button>
      {error && <span style={{ color: 'red', marginTop: 8 }}>{error}</span>}
    </form>
  );
};

export default LoginForm;
