import React, { useRef, useEffect, useState, useContext } from 'react';
import { WebSocketContext } from '../context/WebSocketContext';

const CANVAS_BG = '#f9fafb';
const COLORS = ['#e53e3e', '#3182ce', '#38a169', '#f6ad55', '#2d3748', '#555'];
const THICKNESS = [2, 4, 6, 8, 12];
const TOOLS = [
  { key: 'brush', label: 'Brush' },
  { key: 'eraser', label: 'Eraser' },
  { key: 'rectangle', label: 'Rectangle' },
  { key: 'ellipse', label: 'Ellipse' },
  { key: 'text', label: 'Text' },
  { key: 'undo', label: 'Undo' }
];

const Canvas = () => {
  const canvasRef = useRef(null);
  const [drawing, setDrawing] = useState(false);
  const [lastPosition, setLastPosition] = useState({ x: null, y: null });
  const [color, setColor] = useState(COLORS[1]);
  const [thickness, setThickness] = useState(4);
  const [tool, setTool] = useState('brush');
  const [shapes, setShapes] = useState([]);
  const [shapeStart, setShapeStart] = useState(null);
  const context = useContext(WebSocketContext);
  const wsRef = context?.wsRef;

  // Define drawStroke FIRST, always before use!
  const drawStroke = (stroke) => {
    const ctx = canvasRef.current.getContext('2d');
    if (stroke.type === 'draw' || stroke.type === 'brush' || stroke.type === 'eraser') {
      ctx.beginPath();
      ctx.moveTo(stroke.fromX, stroke.fromY);
      ctx.lineTo(stroke.toX, stroke.toY);
      ctx.strokeStyle = stroke.color;
      ctx.lineWidth = stroke.thickness;
      ctx.stroke();
    }
    if (stroke.type === 'rectangle') {
      ctx.strokeStyle = stroke.color;
      ctx.lineWidth = stroke.thickness;
      ctx.strokeRect(stroke.fromX, stroke.fromY,
        stroke.toX - stroke.fromX, stroke.toY - stroke.fromY);
    }
    if (stroke.type === 'ellipse') {
      ctx.strokeStyle = stroke.color;
      ctx.lineWidth = stroke.thickness;
      ctx.beginPath();
      ctx.ellipse(
        (stroke.fromX + stroke.toX) / 2,
        (stroke.fromY + stroke.toY) / 2,
        Math.abs((stroke.toX - stroke.fromX) / 2),
        Math.abs((stroke.toY - stroke.fromY) / 2),
        0, 0, 2 * Math.PI
      );
      ctx.stroke();
    }
    if (stroke.type === 'text') {
      ctx.fillStyle = stroke.color;
      ctx.font = `${stroke.fontSize || 20}px sans-serif`;
      ctx.fillText(stroke.value, stroke.x, stroke.y);
    }
  };

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas || !wsRef || !wsRef.current) return;
    const ctx = canvas.getContext('2d');
    canvas.width = window.innerWidth * 0.8;
    canvas.height = window.innerHeight * 0.6;
    ctx.lineCap = 'round';

    const receive = event => {
      const msg = JSON.parse(event.data);
      if (msg.type === "init" && Array.isArray(msg.history)) {
        msg.history.forEach(drawStroke);
      } else if (
        msg.type === "draw" || msg.type === "brush" || msg.type === "eraser"
        || msg.type === "rectangle" || msg.type === "ellipse" || msg.type === "text"
      ) {
        drawStroke(msg);
        setShapes(shapes => [...shapes, msg]);
      } else if (msg.type === "undo") {
        setShapes(shapes => shapes.slice(0, -1));
        ctx.clearRect(0, 0, canvas.width, canvas.height);
        shapes.slice(0, -1).forEach(drawStroke);
      }
    };

    const socket = wsRef.current;
    socket.addEventListener('message', receive);
    return () => {
      socket.removeEventListener('message', receive);
    };
    // eslint-disable-next-line
  }, [wsRef]);

  const handleUndo = () => {
    setShapes(shapes => shapes.slice(0, -1));
    wsRef.current.send(JSON.stringify({ type: 'undo' }));
    const ctx = canvasRef.current.getContext('2d');
    ctx.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);
    shapes.slice(0, -1).forEach(drawStroke);
  };

  const start = e => {
    if (tool === 'undo') {
      handleUndo();
      setTool('brush');
      return;
    }
    if (tool === 'text') {
      const { offsetX, offsetY } = e.nativeEvent;
      const value = window.prompt("Enter your text:");
      if (value) {
        const ctx = canvasRef.current.getContext('2d');
        ctx.fillStyle = color;
        ctx.font = '20px sans-serif';
        ctx.fillText(value, offsetX, offsetY);
        const textStroke = { type: 'text', x: offsetX, y: offsetY, value, color, fontSize: 20 };
        setShapes(shapes => [...shapes, textStroke]);
        wsRef.current.send(JSON.stringify(textStroke));
      }
      return;
    }
    if (tool === 'rectangle' || tool === 'ellipse') {
      setShapeStart({ x: e.nativeEvent.offsetX, y: e.nativeEvent.offsetY });
      setDrawing(true);
      return;
    }
    const { offsetX, offsetY } = e.nativeEvent;
    const ctx = canvasRef.current.getContext('2d');
    ctx.strokeStyle = tool === 'eraser' ? CANVAS_BG : color;
    ctx.lineWidth = thickness;
    ctx.beginPath();
    ctx.moveTo(offsetX, offsetY);
    setDrawing(true);
    setLastPosition({ x: offsetX, y: offsetY });
  };

  const move = e => {
    if (!drawing) return;
    if (tool === 'rectangle' || tool === 'ellipse') {
      return; // preview can be added here
    }
    const { offsetX, offsetY } = e.nativeEvent;
    const ctx = canvasRef.current.getContext('2d');
    const drawColor = tool === 'eraser' ? CANVAS_BG : color;
    ctx.strokeStyle = drawColor;
    ctx.lineWidth = thickness;
    ctx.lineTo(offsetX, offsetY);
    ctx.stroke();
    if (wsRef && wsRef.current && wsRef.current.readyState === 1 && lastPosition.x !== null) {
      wsRef.current.send(JSON.stringify({
        type: tool,
        fromX: lastPosition.x,
        fromY: lastPosition.y,
        toX: offsetX,
        toY: offsetY,
        color: drawColor,
        thickness
      }));
      setShapes(shapes => [...shapes, {
        type: tool,
        fromX: lastPosition.x,
        fromY: lastPosition.y,
        toX: offsetX,
        toY: offsetY,
        color: drawColor,
        thickness
      }]);
    }
    setLastPosition({ x: offsetX, y: offsetY });
  };

  const stop = e => {
    setDrawing(false);
    setLastPosition({ x: null, y: null });
    if (tool === 'rectangle' || tool === 'ellipse') {
      if (!shapeStart) return;
      const { offsetX, offsetY } = e.nativeEvent;
      const newShape = {
        type: tool,
        fromX: shapeStart.x,
        fromY: shapeStart.y,
        toX: offsetX,
        toY: offsetY,
        color,
        thickness
      };
      setShapes(shapes => [...shapes, newShape]);
      wsRef.current.send(JSON.stringify(newShape));
      const ctx = canvasRef.current.getContext('2d');
      if (tool === 'rectangle') {
        ctx.strokeStyle = color;
        ctx.lineWidth = thickness;
        ctx.strokeRect(shapeStart.x, shapeStart.y, offsetX - shapeStart.x, offsetY - shapeStart.y);
      } else {
        ctx.strokeStyle = color;
        ctx.lineWidth = thickness;
        ctx.beginPath();
        ctx.ellipse(
          (shapeStart.x + offsetX) / 2, (shapeStart.y + offsetY) / 2,
          Math.abs((offsetX - shapeStart.x) / 2), Math.abs((offsetY - shapeStart.y) / 2),
          0, 0, 2 * Math.PI
        );
        ctx.stroke();
      }
      setShapeStart(null);
    }
  };

  return (
    <div style={{ width: '100%', display: 'flex', flexDirection: 'column', alignItems: 'center', gap: 18 }}>
      <div style={{
        background: '#fff',
        borderRadius: 25,
        border: '1px solid #ddd',
        boxShadow: '0 1px 8px #ececec',
        padding: '6px 16px',
        marginBottom: 8,
        display: 'flex',
        alignItems: 'center',
        gap: 16
      }}>
        <span style={{ fontSize: 14 }}>Tool</span>
        <select
          value={tool}
          style={{
            marginLeft: 8,
            padding: '3px 7px',
            borderRadius: 8,
            border: '1px solid #bbb',
            fontSize: 14
          }}
          onChange={e => setTool(e.target.value)}
        >
          {TOOLS.map(t => (
            <option key={t.key} value={t.key}>
              {t.label}
            </option>
          ))}
        </select>
        <span style={{ fontSize: 14, marginLeft: 8 }}>Color</span>
        {COLORS.map(c =>
          <button
            key={c}
            style={{
              width: 24,
              height: 24,
              borderRadius: 12,
              margin: '0 2px',
              border: color === c ? '2px solid #222' : '1px solid #ccc',
              background: c,
              cursor: 'pointer'
            }}
            onClick={() => { setColor(c); setTool('brush'); }}
          />
        )}
        <span style={{ fontSize: 14, marginLeft: 8 }}>Line</span>
        <select
          value={thickness}
          style={{
            marginLeft: 8,
            padding: '3px 7px',
            borderRadius: 8,
            border: '1px solid #bbb',
            fontSize: 14
          }}
          onChange={e => setThickness(Number(e.target.value))}
        >
          {THICKNESS.map(t =>
            <option key={t} value={t}>{t}px</option>
          )}
        </select>
      </div>
      <canvas
        ref={canvasRef}
        className={tool === 'eraser' ? 'eraser-cursor' : ''}
        style={{
          border: '2px solid #3182ce',
          background: CANVAS_BG,
          borderRadius: '11px',
          cursor: tool === 'eraser'
            ? 'url("data:image/svg+xml,<svg width=\'32\' height=\'32\' fill=\'none\' xmlns=\'http://www.w3.org/2000/svg\'><rect x=\'7\' y=\'7\' width=\'18\' height=\'10\' rx=\'4\' fill=\'pink\' stroke=\'gray\' stroke-width=\'2\'/></svg>") 16 16, pointer'
            : drawing ? 'crosshair' : 'pointer',
          boxShadow: '0 2px 18px #dae3ea'
        }}
        onMouseDown={start}
        onMouseMove={move}
        onMouseUp={stop}
        onMouseLeave={stop}
      />
    </div>
  );
};

export default Canvas;
