import React, { useRef, useEffect, useState, useContext } from 'react';
import { WebSocketContext } from '../context/WebSocketContext';

const CANVAS_BG = '#f9fafb';
const COLORS = ['#e53e3e', '#3182ce', '#38a169', '#f6ad55', '#2d3748', '#555'];
const THICKNESS = [2, 4, 6, 8, 12];
const TOOLS = [
  { key: 'brush', label: 'Brush' },
  { key: 'eraser', label: 'Eraser' }
];

const Canvas = () => {
  const canvasRef = useRef(null);
  const [drawing, setDrawing] = useState(false);
  const [lastPosition, setLastPosition] = useState({ x: null, y: null });
  const [color, setColor] = useState(COLORS[1]);
  const [thickness, setThickness] = useState(4);
  const [tool, setTool] = useState('brush');
  const context = useContext(WebSocketContext);
  const wsRef = context?.wsRef;

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas || !wsRef || !wsRef.current) return;
    const ctx = canvas.getContext('2d');
    canvas.width = window.innerWidth * 0.8;
    canvas.height = window.innerHeight * 0.6;
    ctx.lineCap = 'round';

    function drawStroke(stroke) {
      ctx.beginPath();
      ctx.moveTo(stroke.fromX, stroke.fromY);
      ctx.lineTo(stroke.toX, stroke.toY);
      ctx.strokeStyle = stroke.color;
      ctx.lineWidth = stroke.thickness;
      ctx.stroke();
    }

    const receive = event => {
      const msg = JSON.parse(event.data);
      if (msg.type === "init" && Array.isArray(msg.history)) {
        msg.history.forEach(drawStroke);
      } else if (msg.type === "draw") {
        drawStroke(msg);
      }
    };

    const socket = wsRef.current;
    socket.addEventListener('message', receive);
    return () => {
      socket.removeEventListener('message', receive);
    };
  }, [wsRef]);

  const start = e => {
    const { offsetX, offsetY } = e.nativeEvent;
    const ctx = canvasRef.current.getContext('2d');
    ctx.strokeStyle = tool === 'eraser' ? CANVAS_BG : color;
    ctx.lineWidth = thickness;
    ctx.beginPath();
    ctx.moveTo(offsetX, offsetY);
    setDrawing(true);
    setLastPosition({ x: offsetX, y: offsetY });
  };

  const move = e => {
    if (!drawing) return;
    const { offsetX, offsetY } = e.nativeEvent;
    const ctx = canvasRef.current.getContext('2d');
    const drawColor = tool === 'eraser' ? CANVAS_BG : color;
    ctx.strokeStyle = drawColor;
    ctx.lineWidth = thickness;
    ctx.lineTo(offsetX, offsetY);
    ctx.stroke();
    if (wsRef && wsRef.current && wsRef.current.readyState === 1 && lastPosition.x !== null) {
      wsRef.current.send(JSON.stringify({
        type: 'draw',
        fromX: lastPosition.x,
        fromY: lastPosition.y,
        toX: offsetX,
        toY: offsetY,
        color: drawColor,
        thickness
      }));
    }
    setLastPosition({ x: offsetX, y: offsetY });
  };

  const stop = () => {
    setDrawing(false);
    setLastPosition({ x: null, y: null });
  };

  return (
    <div style={{ width: '100%', display: 'flex', flexDirection: 'column', alignItems: 'center', gap: 18 }}>
      <div style={{
        background: '#fff',
        borderRadius: 25,
        border: '1px solid #ddd',
        boxShadow: '0 1px 8px #ececec',
        padding: '6px 16px',
        marginBottom: 8,
        display: 'flex',
        alignItems: 'center',
        gap: 16
      }}>
        <span style={{ fontSize: 14 }}>Tool</span>
        <select
          value={tool}
          style={{
            marginLeft: 8,
            padding: '3px 7px',
            borderRadius: 8,
            border: '1px solid #bbb',
            fontSize: 14
          }}
          onChange={e => setTool(e.target.value)}
        >
          {TOOLS.map(t => (
            <option key={t.key} value={t.key}>
              {t.label}
            </option>
          ))}
        </select>
        <span style={{ fontSize: 14, marginLeft: 8 }}>Color</span>
        {COLORS.map(c =>
          <button
            key={c}
            style={{
              width: 24,
              height: 24,
              borderRadius: 12,
              margin: '0 2px',
              border: color === c ? '2px solid #222' : '1px solid #ccc',
              background: c,
              cursor: 'pointer'
            }}
            onClick={() => { setColor(c); setTool('brush'); }}
          />
        )}
        <span style={{ fontSize: 14, marginLeft: 8 }}>Line</span>
        <select
          value={thickness}
          style={{
            marginLeft: 8,
            padding: '3px 7px',
            borderRadius: 8,
            border: '1px solid #bbb',
            fontSize: 14
          }}
          onChange={e => setThickness(Number(e.target.value))}
        >
          {THICKNESS.map(t =>
            <option key={t} value={t}>{t}px</option>
          )}
        </select>
      </div>
      <canvas
        ref={canvasRef}
        style={{
          border: '2px solid #3182ce',
          background: CANVAS_BG,
          borderRadius: '11px',
          cursor: drawing ? 'crosshair' : 'pointer',
          boxShadow: '0 2px 18px #dae3ea'
        }}
        onMouseDown={start}
        onMouseMove={move}
        onMouseUp={stop}
        onMouseLeave={stop}
      />
    </div>
  );
};

export default Canvas;
