// src/components/LiveCaptions.js
import React, { useState, useEffect, useRef, useContext } from 'react';
import { WebSocketContext } from '../context/WebSocketContext';
import './LiveCaptions.css';

// CORE LAYOUT ONLY: STUB for further integration!
const LiveCaptions = ({ username, isEnabled }) => {
  // Minimal layout, later we will implement full live captions
  const [captions, setCaptions] = useState([]);
  return (
    <div className="captions-overlay">
      {/* Example placeholder */}
      {captions.length === 0 && isEnabled && (
        <div className="caption-item interim">
          <span className="caption-username">{username}:</span>{' '}
          <span className="caption-text">Say something to see live captions...</span>
        </div>
      )}
    </div>
  );
};

export default LiveCaptions;
