import React, { useState } from 'react';
import LoginForm from './components/LoginForm';
import RegisterForm from './components/RegisterForm';
import RoomList from './components/RoomList';
import Canvas from './components/Canvas'; // Keep old one for now
import { WebSocketProvider } from './context/WebSocketContext';
import './App.css';

const App = () => {
  const [view, setView] = useState('login');
  const [user, setUser] = useState(null);
  const [roomInfo, setRoomInfo] = useState({ name: '', adminUsername: '' });

  const getFirstName = (fullName) => {
    if (!fullName) return '';
    return fullName.trim().split(' ')[0];
  };

  const handleLogin = (userData) => {
    const firstName = getFirstName(userData.fullName || userData.username);
    setUser({ ...userData, firstName });
    setView('room');
  };

  const handleRegister = () => {
    setView('login');
  };

  const handleJoinRoom = (room) => {
    setRoomInfo({
      name: typeof room === 'string' ? room : room.name,
      adminUsername: room.admin_username || room.adminUsername
    });
    setView('canvas');
  };

  const handleSwitchRoom = () => {
    setView('room');
  };

  const renderHeader = () => (
    <header style={{
      padding: '16px 0 0 0',
      textAlign: 'center',
      fontWeight: 700,
      fontSize: '1.8rem',
      color: '#2d3748',
      letterSpacing: '0.5px'
    }}>
      {user?.firstName ? `${user.firstName}'s Real-Time Collaborative Canvas` : 'Real-Time Collaborative Canvas'}
    </header>
  );

  return (
    <WebSocketProvider roomId={roomInfo.name}>
      <div style={{
        background: '#f6f7fb',
        minHeight: '100vh',
        paddingBottom: '48px'
      }}>
        {view !== 'login' && view !== 'register' && renderHeader()}

        <main style={{
          margin: '32px auto',
          maxWidth: '980px',
          minHeight: '500px',
          background: '#fff',
          borderRadius: '18px',
          boxShadow: '0 3px 24px #e1e8ed',
          padding: '28px',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center'
        }}>
          {view === 'login' && (
            <LoginForm
              onSuccess={handleLogin}
              onSwitch={() => setView('register')}
            />
          )}

          {view === 'register' && (
            <RegisterForm
              onSuccess={handleRegister}
              onSwitch={() => setView('login')}
            />
          )}

          {view === 'room' && (
            <RoomList
              user={user}
              onJoinRoom={handleJoinRoom}
            />
          )}

          {view === 'canvas' && (
            <Canvas
              user={user}
              roomId={roomInfo.name}
              adminUsername={roomInfo.adminUsername}
              onSwitchRoom={handleSwitchRoom}
            />
          )}
        </main>
      </div>
    </WebSocketProvider>
  );
};

export default App;
