import React, { createContext, useEffect, useState, useRef } from 'react';

export const WebSocketContext = createContext(null);

export const WebSocketProvider = ({ children }) => {
  const [token, setToken] = useState(null); // Store JWT token
  const wsRef = useRef(null);

  useEffect(() => {
    if (!token) return;

    // Connect with token in query string
    const socket = new WebSocket(`ws://localhost:8000/ws?token=${token}`);
    wsRef.current = socket;

    socket.onopen = () => {
      console.log('WebSocket Connected');
    };

    socket.onclose = () => {
      console.log('WebSocket Disconnected');
      setTimeout(() => {
        if (token) {
          wsRef.current = new WebSocket(`ws://localhost:8000/ws?token=${token}`);
        }
      }, 3000);
    };

    return () => {
      socket.close();
    };
  }, [token]);

  return (
    <WebSocketContext.Provider value={{ ws: wsRef.current, setToken }}>
      {children}
    </WebSocketContext.Provider>
  );
};
