import React, { createContext, useEffect, useRef, useState } from 'react';

export const WebSocketContext = createContext(null);

export const WebSocketProvider = ({ roomId, children }) => {
  const wsRef = useRef(null);
  const [wsStatus, setWsStatus] = useState('disconnected');

  useEffect(() => {
    let socket;
    let reconnectTimeout = null;

    function connect() {
      if (!roomId) return;
      console.log(`Connecting to ws://localhost:8000/ws/${roomId}`);
      socket = new WebSocket(`ws://localhost:8000/ws/${roomId}`);
      wsRef.current = socket;

      setWsStatus('connecting');
      socket.onopen = () => setWsStatus('connected');
      socket.onclose = () => {
        setWsStatus('disconnected');
        reconnectTimeout = setTimeout(connect, 3000);
      };
      socket.onerror = () => {
        setWsStatus('error');
        socket.close();
      };
    }

    connect();

    return () => {
      if (reconnectTimeout) clearTimeout(reconnectTimeout);
      if (wsRef.current) wsRef.current.close();
      wsRef.current = null;
      setWsStatus('disconnected');
    };
  }, [roomId]); // Reconnect if roomId changes

  return (
    <WebSocketContext.Provider value={{ wsRef, wsStatus }}>
      {children}
    </WebSocketContext.Provider>
  );
};
