import React, { useRef, useEffect, useState, useContext } from 'react';
import { WebSocketContext } from '../context/WebSocketContext';
import './Canvas.css';

const COLORS = ['#e53e3e', '#3182ce', '#38a169', '#f6ad55', '#2d3748', '#555'];
const THICKNESS = [2, 4, 6, 8, 12];
const TOOLS = [
  { key: 'brush', label: 'Brush' },
  { key: 'eraser', label: 'Eraser' },
  { key: 'rectangle', label: 'Rectangle' },
  { key: 'ellipse', label: 'Ellipse' },
  { key: 'text', label: 'Text' },
  { key: 'undo', label: 'Undo' }
];
const CURSORS = { /* ...cursors as before... */ };
const CANVAS_W = 1200;
const CANVAS_H = 700;

const Canvas = ({ user, roomId, onSwitchRoom }) => {
  // ... all your existing hooks, helpers, etc. ...
  const canvasRef = useRef(null);
  const [drawing, setDrawing] = useState(false);
  const [lastPosition, setLastPosition] = useState({ x: null, y: null });
  const [color, setColor] = useState(COLORS[1]);
  const [thickness, setThickness] = useState(4);
  const [tool, setTool] = useState('brush');
  const [shapes, setShapes] = useState([]);
  const [shapeStart, setShapeStart] = useState(null);
  const [cursors, setCursors] = useState({});
  const { wsRef, wsStatus } = useContext(WebSocketContext);

  // ... user presence and conflict helpers here ...

  const sendWS = (obj) => {
    if (wsRef && wsRef.current && wsRef.current.readyState === 1) {
      wsRef.current.send(JSON.stringify(obj));
    }
  };

  // ... getCanvasCoords, drawStroke, and undo logic as before ...

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas || !wsRef || !wsRef.current) return;
    canvas.width = CANVAS_W;
    canvas.height = CANVAS_H;
    const ctx = canvas.getContext('2d');
    ctx.lineCap = 'round';

    const receive = event => {
      const msg = JSON.parse(event.data);
      if (msg.type === "init" && Array.isArray(msg.history)) {
        msg.history.forEach(drawStroke);
      } else if (
        ['draw', 'brush', 'eraser', 'rectangle', 'ellipse', 'text'].includes(msg.type)
      ) {
        drawStroke(msg);
        setShapes(shapes => [...shapes, msg]);
      } else if (msg.type === "undo") {
        setShapes(shapes => {
          const result = shapes.slice(0, -1);
          ctx.clearRect(0, 0, CANVAS_W, CANVAS_H);
          result.forEach(drawStroke);
          return result;
        });
      }
      // --- CLEAR SCREEN LOGIC ---
      else if (msg.type === "clear") {
        setShapes([]);
        ctx.clearRect(0, 0, CANVAS_W, CANVAS_H);
      }
      // --- User presence logic for cursors ---
      else if (msg.type === "cursor" && msg.userId && msg.x != null && msg.y != null) {
        setCursors(prev => ({
          ...prev,
          [msg.userId]: { x: msg.x, y: msg.y, name: msg.name, tool: msg.tool }
        }));
      }
    };
    const socket = wsRef.current;
    socket.addEventListener('message', receive);
    return () => socket && socket.removeEventListener('message', receive);
  }, [wsRef, user]);

  // ...your handleUndo, start, move, stop, etc...

  // Toolbar, including "Clear Screen" button:
  return (
    <div style={{ width: '100%', display: 'flex', flexDirection: 'column', alignItems: 'center', gap: 18 }}>
      {/* ...presence/user bar and connection status here... */}
      <div className="canvas-title">
        Room: {roomId}
      </div>
      <div className="canvas-toolbar">
        {/* ...Tool, color, thickness selectors... */}
        <button
          className="canvas-clearscreen-btn"
          style={{
            marginLeft: 12,
            padding: '8px 14px',
            borderRadius: 8,
            background: '#e53e3e',
            color: '#fff',
            fontWeight: 600,
            border: 'none',
            cursor: 'pointer'
          }}
          onClick={() => {
            sendWS({ type: 'clear' });
            const ctx = canvasRef.current.getContext('2d');
            ctx.clearRect(0, 0, CANVAS_W, CANVAS_H);
            setShapes([]);
          }}
        >
          Clear Screen
        </button>
        {onSwitchRoom &&
          <button
            className="canvas-switchroom-btn"
            style={{
              marginLeft: 12,
              padding: '8px 14px',
              borderRadius: 8,
              background: '#3182ce',
              color: '#fff',
              fontWeight: 600,
              border: 'none',
              cursor: 'pointer'
            }}
            onClick={onSwitchRoom}
          >
            Switch Room
          </button>
        }
      </div>
      <div style={{ position: 'relative', width: CANVAS_W, height: CANVAS_H }}>
        <canvas
          ref={canvasRef}
          className="canvas-canvas-area"
          width={CANVAS_W}
          height={CANVAS_H}
          style={{ cursor: CURSORS[tool] || 'crosshair' }}
          onMouseDown={start}
          onMouseMove={move}
          onMouseUp={stop}
          onMouseLeave={stop}
        />
        {/* ...claims and other cursors... */}
      </div>
    </div>
  );
};

export default Canvas;
